import "./App.css";
import { useState, useEffect } from "react";

function App() {
  const [searchItem, setSearchItem] = useState("");
  const [showList, setShowList] = useState([]);
  const [loading, setLoading] = useState(true);
  const [filter, setFilter] = useState("shows");
  const [firstRender, setFirstRender] = useState(true);
  let noImage =
    "https://upload.wikimedia.org/wikipedia/commons/6/65/No-Image-Placeholder.svg";

  function handleSubmit(e) {
    e.preventDefault();

    const val = e.target.value;
    console.log("value", val, e);
    setSearchItem(val);
  }

  // fake the mock API
  // sleep
  function sleep(time) {
    const promise = new Promise((res, rej) => {
      setTimeout(() => res(), time);
    });
    return promise;
  }
  async function getTheMovies(searchTerm) {
    await sleep(1000);
    const url = `https://api.tvmaze.com/search/shows?q=${searchTerm}`;
    const response = await fetch(url);
    const data = await response.json();
    console.log(data, "data");

    setFirstRender(false);
    setShowList(data);
  }

  useEffect(() => {
    return (
      document.getElementById("display").style.display = "block"
    )
  },[searchItem])

  useEffect(() => {
    (async () => {
      setLoading(true);
      await getTheMovies(searchItem); // 3 seconds
      setLoading(false);
    })();
  }, [searchItem]);

  function displayShowUi() {
    return (
      showList &&
      showList?.map((showObj) => {
        return (
          <>
            <div className="movies">
              <div className="movieInfo">
                <h4>Name: {showObj.show.name}</h4>
                <p></p>
              </div>
              <img
                src={
                  showObj.show.image === null
                    ? noImage
                    : showObj.show.image.original
                }
                alt="poster"
                className="image"
              />
            </div>
          </>
        );
      })
    );
  }

  return (
    <>
      <div className="home">
        <h1 className="title">Show Stalker</h1>
        <h4>Search your favourite shows</h4>
        <input type={"radio"} name={"filter"} value={"Actor"} />
        Actor
        <input type={"radio"} name={"filter"} value={"Actor"} />
        Shows
        <form>
          <input
            name="searchBox"
            type={"text"}
            placeholder="ENTER THE MOVIE NAME"
            onChange={handleSubmit}
          />
        </form>
      </div>
      {/* result list */}
      <div id="display">
        {!loading && (
          <div className="displayMovie">
            {displayShowUi().length ? displayShowUi() : <>No results found</>}
          </div>
        )}
        {loading && !firstRender && <div>Loading .... . .... .... .... </div>}
      </div>
    </>
  );
}

export default App;
