import React, { useState } from 'react'

function Poster(props) {
    const {setShowFilter} = props;
    const [height, setHeight] = useState('100vh')

    const homePageStyle = {

        height : height
    }
    function changePoster() {
        document.getElementById('home-btn').style.display = 'none';
        // console.log(document.getElementById('main'));

        const main = document.getElementById('main').style;
        main.setProperty('background-image', 'url()');
        main.backgroundColor = 'black';
        main.fontSize = '40px';
        setHeight('20vh');
        setShowFilter(true);
    }

  return (
    <div className="poster-main" style={homePageStyle} id={"main"}>
          <h1 className="title" id={"title"}>Show Stalker</h1>
          <button onClick={() => changePoster()} id={"home-btn"}>Let's Search</button>
        </div>
  )
}

export default Poster
