import "./App.css";
import { useState, useEffect } from "react";
import Show from "../Show/Show";
import NoResult from "../NoResult/NoResult";

function App() {
  const [searchItem, setSearchItem] = useState("");
  const [showList, setShowList] = useState([]);
  const [loading, setLoading] = useState(true);
  const [filter, setFilter] = useState("shows");

  function handleSubmit(e) {
    const val = e.target.value;
    setSearchItem(val);
  }

  async function getTheShows(searchItem) {
    console.log("api");
    let data, response, url;
    url = `https://api.tvmaze.com/search/${filter}?q=${searchItem}`;
    if (filter === "shows") {
      response = await fetch(url);
      data = await response.json();
    } else {
      url = `https://api.tvmaze.com/search/${filter}?q=${searchItem}`;
      response = await fetch(url);
      data = await response.json();
    }
    setShowList(data);
  }

  useEffect(() => {
    (async () => {
      setLoading(true);
      await getTheShows(searchItem);
      setLoading(false);
    })();
  }, [searchItem, filter]);

  function changeFilter(e) {
    setFilter(e.target.value);
    setShowList([]);
  }

  return (
    <>
      <div className="home">
        <h1 className="title">Show Stalker</h1>
        <h4>Search for your favourite shows</h4>
        <div className="filter">
          <div className="option">
            <input
              type={"radio"}
              name={"filter"}
              value={"people"}
              onClick={changeFilter}
              id={"people"}
            />
            <span className="filterName">Actor</span>
          </div>
          <div className="option">
            <input
              type={"radio"}
              name={"filter"}
              value={"shows"}
              onClick={changeFilter}
              id={"show"}
              defaultChecked={true}
            />
            <span className="filterName">Shows</span>
          </div>
        </div>
        <form>
          <input
            id="searchBox"
            name="searchBox"
            type={"text"}
            placeholder="e.g The Witcher, Panchayat"
            onChange={handleSubmit}
          />
        </form>
        <div className="actorInfo">
          {filter === "people" && (
            <h3>Click on actor below to display their shows</h3>
          )}
        </div>
      </div>

      {/* result list */}
      <div>
        {!loading && (
          <div className="displayShows">
            {showList.length ? (
              <Show filter={filter} showList={showList} />
            ) : (
              <NoResult />
            )}
          </div>
        )}
        {loading && <div>Loading .... . .... .... .... </div>}
      </div>
    </>
  );
}

export default App;
