import React from 'react'

function Show(props) {
    console.log("child");
    const {filter, showList, setShowList} = props;
    let noImage =
    "https://upload.wikimedia.org/wikipedia/commons/6/65/No-Image-Placeholder.svg";
    // async function getTheShows(searchItem) {
    //   console.log("api");
    //   let data, response, url;
    //   url = `https://api.tvmaze.com/search/${filter}?q=${searchItem}`;
    //   if (filter === "shows") {
    //     response = await fetch(url);
    //     data = await response.json();
    //   } else {
    //     url = `https://api.tvmaze.com/search/${filter}?q=${searchItem}`;
    //     response = await fetch(url);
    //     data = await response.json();
    //   }
    //   setShowList(data);
    // }

    async function showsOfActor(actor) {
      const url = `https://api.tvmaze.com/people/${actor}/castcredits?embed=show`
      const response = await fetch(url)
      const data = await response.json();
      setShowList(data);
    }
    // console.log(showList);

    if(filter === "shows") {
        return (
            showList &&
            showList?.map((showObj) => {
              return (
                <>
                  <div className="shows" key={showObj}>
                    <img
                      src={
                        showObj.show.image === null
                          ? noImage
                          : showObj.show.image.original
                      }
                      alt="poster"
                      className="image"
                    />
                    <div className="showInfo">
                      <h3>{showObj.show.name}</h3>
                      <h4>Language : {showObj.show.language}</h4>
                    </div>
                  </div>
                </>
              );
            })
          );
    } else {
        return (
            showList &&
            showList?.map((showObj) => {
              return (
                <>
                  <div className="shows" key={showObj}>
                    <img onClick={() => showsOfActor(showObj.person.id)}
                      src={
                        showObj.person.image === null
                          ? noImage
                          : showObj.person.image.original
                      }
                      alt="poster"
                      className="image"
                    />
                    <div className="showInfo">
                      <h3>{showObj.person.name}</h3>
                    </div>
                  </div>
                </>
              );
            })
          );
    }


}

export default Show
