import "./App.css";
import { useState, useEffect } from "react";

function App() {
  const [searchItem, setSearchItem] = useState("");
  const [showList, setShowList] = useState([]);
  const [loading, setLoading] = useState(true);
  const [filter, setFilter] = useState("people");
  // const [placeHolder, setPlaceHolder] = useState()

  // const placeHolderArr = [
  //   "e.g The Witcher, Panchayat",
  //   "e.g Jennifer Aniston, "
  // ]
  let noImage =
    "https://upload.wikimedia.org/wikipedia/commons/6/65/No-Image-Placeholder.svg";

  function handleSubmit(e) {
    e.preventDefault();
    const val = e.target.value;
    setSearchItem(val);
  }

  async function getTheMovies(searchTerm) {
    const url = `https://api.tvmaze.com/search/${filter}?q=${searchItem}`;
    const response = await fetch(url);
    const data = await response.json();
    console.log(data, "data");
    setShowList(data);
  }

  useEffect(() => {
    (async () => {
      setLoading(true);
      await getTheMovies(searchItem); // 3 seconds
      setLoading(false);
    })();
  }, [searchItem]);

  function displayShowUi() {
    return (
      showList &&
      showList?.map((showObj) => {
        return (
          <>
            <div className="movies">
              {console.log(showObj.person)}

              {/* <img
                src={
                  showObj.person.image === null
                    ? noImage
                    : showObj.show.image.original
                }
                alt="poster"
                className="image"
              /> */}
              {/* <div className="movieInfo">
                <h3>{showObj.show.name}</h3>
                <h4>Language : {showObj.show.language}</h4>
                <p></p>
              </div> */}
            </div>
          </>
        );
      })
    );
  }

  function changeFilter(e) {
    setFilter(e.target.value);
  }

  return (
    <>
      <div className="home">
        <h1 className="title">Show Stalker</h1>
        <h4>Search for your favourite shows</h4>
        <div className="filter">
          <div className="option">
            <input type={"radio"} name={"filter"} value={"people"} onClick={changeFilter}/>
            <span className="filterName">Actor</span>
          </div>
          <div className="option">
            <input type={"radio"} name={"filter"} value={"shows"} onClick={changeFilter} checked/>
            <span className="filterName">Shows</span>
          </div>
        </div>
        <form>
          <input
            name="searchBox"
            type={"text"}
            placeholder="ENTER THE MOVIE NAME"
            onChange={handleSubmit}
            // placeHolder={placeHolder}
          />
        </form>
      </div>
      {/* result list */}
      <div>
        {!loading && (
          <div className="displayMovie">
            {displayShowUi().length ? displayShowUi() : <>No results found</>}
          </div>
        )}
        {loading && <div>Loading .... . .... .... .... </div>}
      </div>
    </>
  );
}

export default App;
