import React, { useState } from "react";

function Filter(props) {
  const { setShowList } = props;

  const [filter, setFilter] = useState("shows");

  let placeholder = ["e.g Loki, Daredevil, Mirzapur", "e.g Kapil Sharma"];

  function changeFilter(e) {
    setFilter(e.target.value);
    setShowList([]);
  }
  return (
    <>
      <div className="filter">
        <h4>Search for your favourite shows</h4>
        <div className="option">
          <input
            type={"radio"}
            name={"filter"}
            value={"people"}
            onClick={changeFilter}
            id={"people"}
          />
          <span className="filterName">Actor</span>
        </div>
        <div className="option">
          <input
            type={"radio"}
            name={"filter"}
            value={"shows"}
            onClick={changeFilter}
            id={"show"}
            defaultChecked={true}
          />
          <span className="filterName">Shows</span>
        </div>
      </div>
      <form>
        <input
          id="searchBox"
          name="searchBox"
          type={"text"}
          placeholder={filter === "shows" ? placeholder[0] : placeholder[1]}
          onChange={handleSubmit}
        />
      </form>
      <div className="actorInfo">
        {filter === "people" && (
          <h3>Click on actor below to display their shows</h3>
        )}
      </div>
    </>
  );
}

export default Filter;
