import React from 'react'

function Show(props) {
    const {filter, showList} = props;
    let noImage =
    "https://upload.wikimedia.org/wikipedia/commons/6/65/No-Image-Placeholder.svg";
    console.log(showList, "last", filter);
    if(filter === "shows") {
        return (
            showList &&
            showList?.map((showObj) => {
              return (
                <>
                  <div className="shows" key={showObj}>
                    <img
                      src={
                        showObj.show.image === null
                          ? noImage
                          : showObj.show.image.original
                      }
                      alt="poster"
                      className="image"
                    />
                    <div className="showInfo">
                      <h3>{showObj.show.name}</h3>
                      <h4>Language : {showObj.show.language}</h4>
                    </div>
                  </div>
                </>
              );
            })
          );
    } else {
        return (
            showList &&
            showList?.map((showObj) => {
              return (
                <>
                  <div className="shows" key={showObj}>
                    <img
                      src={
                        showObj.person.image === null
                          ? noImage
                          : showObj.person.image.original
                      }
                      alt="poster"
                      className="image"
                    />
                    <div className="showInfo">
                      <h3>{showObj.person.name}</h3>
                      {/* <h4>Language : {showObj.show.language}</h4> */}
                    </div>
                  </div>
                </>
              );
            })
          );
    }


}

export default Show
