import React, { useState } from 'react'

function Poster(props) {
    const {setShowFilter} = props;
    const [height, setHeight] = useState('100vh')

    const homePageStyle = {
        height : height
    }
    function changePoster() {
        document.getElementById('home-btn').style.display = 'none';
        document.getElementById('title').style.fontSize = '100px';
        setHeight('30vh');
        setShowFilter(true);
    }

  return (
    <div className="main" style={homePageStyle}>
          <h1 className="title" id={"title"}>Show Stalker</h1>
          <button onClick={() => changePoster()} id={"home-btn"}>Let's Search</button>
        </div>
  )
}

export default Poster
