import "./App.css";
import { useState, useEffect } from "react";

function App() {
  const [searchItem, setSearchItem] = useState("");
  const [showList, setShowList] = useState([]);
  const [loading, setLoading] = useState(false);

  function handleSubmit(e) {
    e.preventDefault();
    const val = e.target.value;
    console.log("value", val, e);
    setSearchItem(val);
  }

  // fake the mock API
  // sleep
  async function getTheMovies(searchTerm) {
    const url = `https://api.tvmaze.com/search/shows?q=${searchTerm}`;
    const response = await fetch(url);
    const data = await response.json();
    console.log(data, "data");
    setShowList(data);
  }

  useEffect(() => {
    (async () => {
      setLoading(true);
      await getTheMovies(searchItem); // 3 seconds
      setLoading(false);
    })();
  }, [searchItem]);

  function displayShowUi() {
    return (
      showList &&
      showList?.map((showObj) => {
        return (
          <>
            <div>Name: {showObj.show.name}</div>
            <div>Runtime: {showObj.show.runtime}</div>
            <div>{showObj.show.image.original}</div>
          </>
        );
      })
    );
  }

  return (
    <>
      <h1>Enter the TV show name</h1>

      <form>
        <input
          name="searchBox"
          type={"text"}
          placeholder="ENTER THE MOVIE NAME"
          onChange={handleSubmit}
          autoComplete='off'
        />
      </form>

      {/* result list */}
      {!loading && (
        <div>
          {displayShowUi().length ? displayShowUi() : <>No results found</>}
        </div>
      )}
      {loading && <div>Loading .... . .... .... .... </div>}
    </>
  );
}

export default App;
