import "./App.css";
import { useState, useEffect } from "react";
import Show from "../Show/Show";
import NoResult from "../NoResult/NoResult";
import HomePage from "../HomePage/HomePage";

function App() {
  const [searchItem, setSearchItem] = useState("");
  const [showList, setShowList] = useState([]);
  const [loading, setLoading] = useState(true);
  const [filter, setFilter] = useState("shows");
  const [maiHeight, setMainHeight] = useState("100vh");
  let placeholder = ["e.g Loki, Daredevil, Mirzapur", "e.g Kapil Sharma"];

  function handleSubmit(e) {
    const val = e.target.value;
    setSearchItem(val);
  }

  async function getTheShows(searchItem) {
    let data, response, url;
    url = `https://api.tvmaze.com/search/${filter}?q=${searchItem}`;
    if (filter === "shows") {
      response = await fetch(url);
      data = await response.json();
    } else {
      url = `https://api.tvmaze.com/search/${filter}?q=${searchItem}`;
      response = await fetch(url);
      data = await response.json();
    }
    setShowList(data);
  }

  useEffect(() => {
    (async () => {
      setLoading(true);
      await getTheShows(searchItem);
      setLoading(false);
    })();
  }, [searchItem, filter]);

  function changeFilter(e) {
    setFilter(e.target.value);
    setShowList([]);
  }

  return (
    <>
      {/* Homepage */}
      <div className="home">
        <HomePage />
        {/* Filter / Options */}
        <div className="filter">
          <h4>Search for your favourite shows</h4>
          <div className="option">
            <input
              type={"radio"}
              name={"filter"}
              value={"people"}
              onClick={changeFilter}
              id={"people"}
            />
            <span className="filterName">Actor</span>
          </div>
          <div className="option">
            <input
              type={"radio"}
              name={"filter"}
              value={"shows"}
              onClick={changeFilter}
              id={"show"}
              defaultChecked={true}
            />
            <span className="filterName">Shows</span>
          </div>
        </div>

        {/* Input Box */}
        <form>
          <input
            id="searchBox"
            name="searchBox"
            type={"text"}
            placeholder={filter === "shows" ? placeholder[0] : placeholder[1]}
            onChange={handleSubmit}
          />
        </form>
        <div className="actorInfo">
          {filter === "people" && (
            <h3>Click on actor below to display their shows</h3>
          )}
        </div>
      </div>

      {/* Results */}
      <div>
        {/* {console.log(showList.length, showList)} */}
        {!loading && (
          <div className="displayShows">
            {showList.length ? (
              <Show
                filter={filter}
                showList={showList}
                setShowList={setShowList}
              />
            ) : (
              <NoResult />
            )}
          </div>
        )}
        {loading && <div>Loading .... . .... .... .... </div>}
      </div>
    </>
  );
}

export default App;
