import "./App.css";
import { useState, useEffect } from "react";
import Movie from "../Movie/Movie";

function App() {
  const [searchItem, setSearchItem] = useState("");
  const [showList, setShowList] = useState([]);
  const [loading, setLoading] = useState(true);
  const [filter, setFilter] = useState("shows");

  function handleSubmit(e) {
    const val = e.target.value; 
    setSearchItem(val);
  }

  async function getTheMovies(searchItem) {
    console.log("api");
    let data, response, url;
    url = `https://api.tvmaze.com/search/${filter}?q=${searchItem}`;
    if (filter === "shows") {
      response = await fetch(url);
      data = await response.json();
    } else {
      url = `https://api.tvmaze.com/search/${filter}?q=${searchItem}`;
      response = await fetch(url);
      data = await response.json();
    }

    console.log(url);
    // console.log(data, "data");
    setShowList(data);
  }

  useEffect(() => {
    (async () => {
      setLoading(true);
      console.log("calling movies")
      await getTheMovies(searchItem);
      setLoading(false);
    })();
  }, [searchItem, filter]);

  function changeFilter(e) {
    setFilter(e.target.value);
    console.log("filter changed");
  }

  return (
    <>
      <div className="home">
        <h1 className="title">Show Stalker</h1>
        <h4>Search for your favourite shows</h4>
        <div className="filter">
          <div className="option">
            <input
              type={"radio"}
              name={"filter"}
              value={"people"}
              onClick={changeFilter}
              id={"people"}
            />
            <span className="filterName">Actor</span>
          </div>
          <div className="option">
            <input
              type={"radio"}
              name={"filter"}
              value={"shows"}
              onClick={changeFilter}
              id={"show"}
              defaultChecked={true}
            />
            <span className="filterName">Shows</span>
          </div>
        </div>
        <form>
          <input
            name="searchBox"
            type={"text"}
            placeholder="e.g The Witcher, Panchayat"
            onChange={handleSubmit}
          />
        </form>
      </div>

      {/* result list */}
      <div>
        {!loading && (
          <div className="displayMovie">
            {showList.length ? <Movie filter={filter} showList={showList} /> : <>No results found</>}
          </div>
        )}
        {loading && <div>Loading .... . .... .... .... </div>}
      </div>
    </>
  );
}

export default App;
