import "./App.css";
import { useState, useEffect } from "react";

function App() {
  const [searchItem, setSearchItem] = useState("");
  const [showList, setShowList] = useState([]);
  const [loading, setLoading] = useState(false);
  let noImage =
    "https://upload.wikimedia.org/wikipedia/commons/6/65/No-Image-Placeholder.svg";

  function handleSubmit(e) {
    e.preventDefault();

    const val = e.target.value;
    console.log("value", val, e);
    setSearchItem(val);
  }

  // fake the mock API
  // sleep
  function sleep(time) {
    const promise = new Promise((res, rej) => {
      setTimeout(() => res(), time);
    });
    return promise;
  }
  async function getTheMovies(searchTerm) {
    await sleep(1000);
    const url = `https://api.tvmaze.com/search/shows?q=${searchTerm}`;
    const response = await fetch(url);
    const data = await response.json();
    console.log(data, "data");
    setShowList(data);
  }

  useEffect(() => {
    (async () => {
      setLoading(true);
      await getTheMovies(searchItem); // 3 seconds
      setLoading(false);
    })();
  }, [searchItem]);

  function displayShowUi() {
    return (
      showList &&
      showList?.map((showObj) => {
        return (
          <>
            <div className="movies">
              Name: {showObj.show.name}
              <img
                src={
                  showObj.show.image === null
                    ? noImage
                    : showObj.show.image.original
                }
                alt="poster"
                className="image"
              />
            </div>
          </>
        );
      })
    );
  }

  return (
    <>
      <h1>Enter the TV show name</h1>

      <form>
        <input
          name="searchBox"
          type={"text"}
          placeholder="ENTER THE MOVIE NAME"
          onChange={handleSubmit}
        />
      </form>

      {/* result list */}
      {!loading && (
        <div className="displayMovie">
          {displayShowUi().length ? displayShowUi() : <>No results found</>}
        </div>
      )}
      {loading && <div>Loading .... . .... .... .... </div>}
    </>
  );
}

export default App;
